import React from "react";
import { withStyles } from "@material-ui/core/styles";
import { Paper, Grid, Button, AppBar, CardContent } from "@material-ui/core";
import axios from "axios";
import Nav from "./Nav.jsx";
import SideBar from "./SideBar.jsx";

const drawerWidth = 240;
const useStyles = theme => ({
  root: {
    display: "flex"
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0
  },
  drawerPaper: {
    width: drawerWidth
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3)
  },
  toolbar: theme.mixins.toolbar
});

class Trip extends React.Component {
  state = {
    trips: []
  };
  componentWillMount() {
    axios
      .get(`${process.env.REACT_APP_API}/trips/${this.props.trip._id}`)
      .then(res => {
        console.log({ trips: res.data });
        this.setState({ trips: res.data });
      })
      .catch(err => console.log({ err }));
  }

  render() {
    const { classes } = this.props;
    return (
      <div className={classes.root}>
        <AppBar position="fixed" className={classes.appBar}>
          <Nav />
        </AppBar>
        <SideBar />
        <Paper className={classes.root}>
          <Grid>
            <main className={classes.content}>
              <div className={classes.toolbar} />
              <img
                src="https://www.traveller.com.au/content/dam/images/1/0/g/g/1/c/image.related.articleLeadwide.620x349.10gduw.png/1415744015369.jpg"
                width="600"
              />
            </main>
            <main className={classes.content}>
              <CardContent title={this.state.trip.title}></CardContent>
              <div className={classes.toolbar} />
              <Button
                size="large"
                type="submit"
                variant="contained"
                color="primary"
                className={classes.submit}
                style={{ background: "#E83350" }}
              >
                I want to join
              </Button>
              <div className={classes.toolbar} />
            </main>
          </Grid>
        </Paper>
      </div>
    );
  }
}

export default withStyles(useStyles)(Trip);
