import React from "react";
import {
  withStyles,
  Drawer,
  CssBaseline,
  List,
  Divider,
  ListItem,
  ListItemIcon,
  ListItemText,
  Link
} from "@material-ui/core";

import AttachMoneyIcon from "@material-ui/icons/AttachMoney";
import LandscapeIcon from "@material-ui/icons/Landscape";
import LocationOnIcon from "@material-ui/icons/LocationOn";
import HourglassEmptyIcon from "@material-ui/icons/HourglassEmpty";
import AddIcon from "@material-ui/icons/Add";

import Input from "@material-ui/core/Input";
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import NativeSelect from "@material-ui/core/NativeSelect";

const drawerWidth = 260;

const useStyles = theme => ({
  root: {
    display: "flex"
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0
  },
  drawerPaper: {
    width: drawerWidth
  },

  formControl: {
    minWidth: 230
  },
  focused: {},
  disabled: {},
  error: {},
  underlineInput: {
    "&:before": {
      // normal
      borderBottom: ".5px solid #E83350"
    },
    "&:after": {
      // focused
      borderBottom: `.5px solid #E83350`
    },
    "&:hover:not($disabled):not($focused):not($error):before": {
      // hover
      borderBottom: `.5px solid #E83350`
    }
  },

  toolbar: theme.mixins.toolbar
});

class SideBar extends React.Component {
  state = {
    budget: ""
  };

  handleChange = name => event => {
    this.setState({ [name]: event.target.value });
  };

  render() {
    const { classes } = this.props;
    return (
      <div className={classes.root}>
        <CssBaseline />

        <Drawer
          className={classes.drawer}
          variant="permanent"
          classes={{
            paper: classes.drawerPaper
          }}
        >
          <div className={classes.toolbar} />
          <Divider />
          <Link href="/CreatePostTrip">
            <List>
              {["Post a Trip"].map(text => (
                <ListItem button key={text}>
                  <ListItemIcon>
                    <AddIcon />
                  </ListItemIcon>
                  <ListItemText primary={text} />
                </ListItem>
              ))}
            </List>
          </Link>

          <Divider />
          <List>
            <ListItem>
              <FormControl className={classes.formControl}>
                <NativeSelect
                  value={this.state.budget}
                  onChange={this.handleChange("budget")}
                  input={
                    <Input
                      classes={{
                        focused: classes.focused,
                        disabled: classes.disabled,
                        error: classes.error,
                        underline: classes.underlineInput
                      }}
                    />
                  }
                  inputProps={{
                    name: "budget",
                    id: "budget-simple"
                  }}
                >
                  <option value="" disabled>
                    Budget (low to high)
                  </option>
                  <option value={10}>Ten</option>
                  <option value={20}>Twenty</option>
                  <option value={30}>Thirty</option>
                </NativeSelect>
              </FormControl>
            </ListItem>
          </List>

          <Divider />
          <List>
            {["Location"].map(text => (
              <ListItem button key={text}>
                <ListItemIcon>
                  <LocationOnIcon />
                </ListItemIcon>
                <ListItemText primary={text} />
              </ListItem>
            ))}
          </List>
          <Divider />
          <List>
            {["Budget (low to high)"].map(text => (
              <ListItem button key={text}>
                <ListItemIcon>
                  <AttachMoneyIcon />
                </ListItemIcon>
                <ListItemText primary={text} />
              </ListItem>
            ))}
          </List>
          <Divider />

          <List>
            {["Duration"].map(text => (
              <ListItem button key={text}>
                <ListItemIcon>
                  <HourglassEmptyIcon />
                </ListItemIcon>
                <ListItemText primary={text} />
              </ListItem>
            ))}
          </List>
          <Divider />

          <List>
            {["Type"].map(text => (
              <ListItem button key={text}>
                <ListItemIcon>
                  <LandscapeIcon />
                </ListItemIcon>
                <ListItemText primary={text} />
              </ListItem>
            ))}
          </List>
          <Divider />

          <Divider />
        </Drawer>
      </div>
    );
  }
}

export default withStyles(useStyles)(SideBar);
